<?php

namespace Soga\NominaBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * NominaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VacacionRepository extends EntityRepository
{   
    /**
     * Devuelve los registros de nomina sin exportar
     * @return type Objeto de tipo query de la clase nomina
     */
    public function DevDqlVacacionSinExportar($strDesde = "", $strHasta = "") {
        $em = $this->getEntityManager();         
        $dql = "SELECT vacacion FROM SogaNominaBundle:Vacacion vacacion WHERE vacacion.exportadoContabilidad = 0";
        if($strDesde != "") {
           $dql = $dql . " AND vacacion.fechap >='". $strDesde ."'" ;
        }
        if($strHasta != "") {
           $dql = $dql . " AND vacacion.fechap <='". $strHasta ."'" ;
        }
        $objQuery = $em->createQuery($dql);       
        return $objQuery;                
    }    
}